import React from "react";
import Slider from "react-slick";
// import "slick-carousel/slick/slick.css";
// import "slick-carousel/slick/slick-theme.css";
import { Button, Grid, Paper } from "@mui/material";
import s from "./style.module.css";
import { green } from "@mui/material/colors";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faGears } from "@fortawesome/free-solid-svg-icons";
import {
  faMicrochip,
  faThumbsUp,
  faTruck,
  faHandshake,
  faChartArea,
} from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

const btnStyle = {
  margin: "5px",
  textAlign: "center",
  padding: "10px",
  borderRadius: "8px",
  border: "1px solid #ccc",
  backgroundColor: "#f0f0f0",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  cursor: "pointer",
  width: "80%",
};

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        width: "50px",
        height: "50px",
        borderRadius: "20px",
        textAlign: "center",
        alignItems: "center",
        justifyContent: "center",
        background: "blue",
        zIndex: 1,
      }}
      onClick={onClick}
    />
  );
}
export default function Products() {
  const images = [
    "https://images.unsplash.com/photo-1508522750368-d7e3edeba646?auto=format&fit=crop&q=80&w=1469&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    "https://images.unsplash.com/photo-1508522750368-d7e3edeba646?auto=format&fit=crop&q=80&w=1469&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    "https://images.unsplash.com/photo-1508522750368-d7e3edeba646?auto=format&fit=crop&q=80&w=1469&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    "https://images.unsplash.com/photo-1508522750368-d7e3edeba646?auto=format&fit=crop&q=80&w=1469&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    "https://images.unsplash.com/photo-1508522750368-d7e3edeba646?auto=format&fit=crop&q=80&w=1469&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
  ];
  const drawerDetails = [
    {
      id: "1",
      title: "title one",
      details: "details lorem lorem lorem",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTE5fPhctwNLodS9VmAniEw_UiLWHgKs0fs1w&usqp=CAU",
    },
    {
      id: "2",
      title: "title two",
      details: "details lorem lorem lorem two",
      image:
        "https://5.imimg.com/data5/OW/AW/SU/SELLER-77199686/ultratech-cement.jpg",
    },
  ];
  var settings = {
    className: "center",
    centerMode: true,
    infinite: true,
    centerPadding: "60px",
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    dots: true,
    arrows: true,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnDotsHover: true,
    pauseOnHover: true,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1440,
        settings: {
          className: "center",
          centerMode: true,
          infinite: true,
          centerPadding: "60px",
          speed: 500,
          slidesToShow: 3,
          slidesToScroll: 1,
          dots: true,
          arrows: true,
          nextArrow: <SampleArrow />,
          prevArrow: <SampleArrow />,
          autoplay: true,
          autoplaySpeed: 2000,
          pauseOnDotsHover: true,
          pauseOnHover: true,
          initialSlide: 0,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 320,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  const [detailIndex, setDetailIndex] = useState(0);
  const buttonData = [
    { icon: faGears, index: 0, label: 'Manufacturing' },
    { icon: faMicrochip, index: 1, label: 'Technology' },
    { icon: faThumbsUp, index: 2, label: 'Top notch quality' },
    { icon: faTruck, index: 3, label: 'Supplier' },
    { icon: faHandshake, index: 4, label: 'Outsource work' },
    { icon: faChartArea, index: 5, label: 'Area of product application' },
  ];
  return (
    <>
      <div className={s.productPage}>
        <div className={s.proudct1}>
          <span className={s.productBtn}>Proudct Usage</span>
          <h1 className={s.productHeading}>Our Products</h1>
        </div>
        <Slider {...settings}>
          {images.map((image, index) => (
            <div key={index} className={s.productcontainer}>
              <img
                src={image}
                alt={`Product ${index}`}
                className={s.productIMG}
                style={{
                  height: "100%",
                  maxWidth:
                    "100%" /* Ensure the image doesn't exceed the container's width */,
                  maxHeight:
                    "100%" /* Ensure the image doesn't exceed the container's height */,
                  display:
                    "block" /* Remove any extra spacing or alignment issues */,
                  margin:
                    "0 auto" /* Center the image horizontally within the div */, // Set the width of the image to 100%
                  borderRadius: "20px",
                }}
              ></img>
            </div>
          ))}
        </Slider>
      </div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr",
          padding: "20px",
          width: "80%",
        }}
      >
       <div
      style={{
        background: 'lightgrey',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {buttonData.map(({ icon, index, label }) => (
        <Button
          key={index}
          sx={btnStyle}
          onClick={() => handleButtonClick(index)}
        >
          <FontAwesomeIcon icon={icon} size="2xl" style={{ marginBottom: '8px' }} />
          <h5 style={{ margin: '0' }}>{label}</h5>
        </Button>
      ))}
    </div> 
        <div style={{ background: "lemonyellow" }}>
          <img
            src={drawerDetails[detailIndex].image}
            alt=""
            style={{ width: "400px", height: "300px" }}
          />
          <h6>{drawerDetails[detailIndex].title}</h6>
          <p>{drawerDetails[detailIndex].details}</p>
        </div>
      </div>
    </>
  );
}
