import React, { useState } from "react";
import Slider from "react-slick";
import { Button, Grid, Paper } from "@mui/material";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faGears,
  faMicrochip,
  faThumbsUp,
  faTruck,
  faHandshake,
  faChartArea,
} from "@fortawesome/free-solid-svg-icons";
import s from "./style.module.css"; // Adjust this import based on your actual stylesheet path

const btnStyle = {
  margin: "5px",
  textAlign: "center",
  padding: "10px",
  borderRadius: "8px",
  border: "1px solid #ccc",
  backgroundColor: "#f0f0f0",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  cursor: "pointer",
  width: "80%",
};

const selectedBtnStyle = {
  ...btnStyle,
  backgroundColor: "darkgrey",
};

function SampleArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{
        ...style,
        display: "flex",
        width: "50px",
        height: "50px",
        borderRadius: "20px",
        textAlign: "center",
        alignItems: "center",
        justifyContent: "center",
        background: "blue",
        zIndex: 1,
      }}
      onClick={onClick}
    />
  );
}

export default function Products() {
  const images = [
    // Your image URLs here
  ];

  const drawerDetails = [
    // Your details here
  ];

  const [detailIndex, setDetailIndex] = useState(0);
  const [selectedButton, setSelectedButton] = useState(null);

  const buttonData = [
    { icon: faGears, index: 0, label: "Manufacturing" },
    { icon: faMicrochip, index: 1, label: "Technology" },
    { icon: faThumbsUp, index: 2, label: "Top notch quality" },
    { icon: faTruck, index: 3, label: "Supplier" },
    { icon: faHandshake, index: 4, label: "Outsource work" },
    { icon: faChartArea, index: 5, label: "Area of product application" },
  ];

  const handleButtonClick = (index) => {
    setSelectedButton(index);
    setDetailIndex(index);
  };

  const settings = {
    className: "center",
    centerMode: true,
    infinite: true,
    centerPadding: "60px",
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    dots: true,
    arrows: true,
    nextArrow: <SampleArrow />,
    prevArrow: <SampleArrow />,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnDotsHover: true,
    pauseOnHover: true,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1440,
        settings: {
          className: "center",
          centerMode: true,
          infinite: true,
          centerPadding: "60px",
          speed: 500,
          slidesToShow: 3,
          slidesToScroll: 1,
          dots: true,
          arrows: true,
          nextArrow: <SampleArrow />,
          prevArrow: <SampleArrow />,
          autoplay: true,
          autoplaySpeed: 2000,
          pauseOnDotsHover: true,
          pauseOnHover: true,
          initialSlide: 0,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 320,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <>
      <div className={s.productPage}>
        <div className={s.proudct1}>
          <span className={s.productBtn}>Product Usage</span>
          <h1 className={s.productHeading}>Our Products</h1>
        </div>
        <Slider {...settings}>
          {images.map((image, index) => (
            <div key={index} className={s.productcontainer}>
              <img
                src={image}
                alt={`Product ${index}`}
                className={s.productIMG}
              />
            </div>
          ))}
        </Slider>
      </div>
      <Grid container spacing={3} sx={{ width: "95%", margin: "auto" }}>
        <Grid item xs={12} md={6}>
          <div className={s.productButtonsContainer}>
            {buttonData.map(({ icon, index, label }) => (
              <Button
                key={index}
                sx={selectedButton === index ? selectedBtnStyle : btnStyle}
                onClick={() => handleButtonClick(index)}
              >
                <FontAwesomeIcon
                  icon={icon}
                  size="2x"
                  style={{ marginBottom: "8px" }}
                />
                <h5 style={{ margin: "0" }}>{label}</h5>
              </Button>
            ))}
          </div>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper className={s.productDetailsContainer}>
            <img
              src={drawerDetails[detailIndex]?.image}
              alt=""
              style={{ width: "100%", height: "300px", borderRadius: "8px" }}
            />
            <h6 className={s.productDetailsTitle}>
              {drawerDetails[detailIndex]?.title}
            </h6>
            <p className={s.productDetailsText}>
              {drawerDetails[detailIndex]?.details}
            </p>
          </Paper>
        </Grid>
      </Grid>
    </>
  );
}
